import React, {Component} from 'react';
import Clarifai from 'clarifai';
import Particles from 'react-particles-js';
import './App.css';
import Navigation from './components/Navigation/Navigation.js';
import Logo from './components/Logo/Logo.js';
import Rank from './components/Rank/Rank.js';
import ImageLinkForm from './components/ImageLinkForm/ImageLinkForm.js';
import FaceRecognition from './components/FaceRecognition/FaceRecognition.js'


const app = new Clarifai.App({
 apiKey: '8f4b63cef0a747c38b676ca789a89a68'
});

const particlesOptions = {
      "particles": {
          "number": {
              "value": 50
          },
          "size": {
              "value": 3
          }
      },
      "interactivity": {
          "events": {
              "onhover": {
                  "enable": true,
                  "mode": "repulse"
              }
          }
      }
  }


class App extends Component {
  constructor () {
    super();
    this.state = {
      input: '', //what users input
      imageUrl: ''
    }
  }

  onInputChange = (event) => {
    this.setState({ input: event.target.value }); //input有變時，把input state更新為輸入值
  }


  onButtonSubmit = () => {
    this.setState({ imageUrl: this.state.input }); //按下按鈕後，將<FaceRecognition />照片更新為input內容
    app.models
      .predict(
        Clarifai.FACE_DETECT_MODEL, 
        'this.state.input')
      .then(
        function(response) {
          console.log(response);
        },
        function(err) {
          // there was an error
        }
      );
  }

  render() {
    return (
      <div className='App'>
        <Particles className='particles' 
          params={particlesOptions} /> 
        <Navigation />
        <Logo />
        <Rank />
        <ImageLinkForm 
          onInputChange={this.onInputChange} //pass as props
          onButtonSubmit={this.onButtonSubmit} //pass as props
        />
        <FaceRecognition 
          imageUrl={this.state.imageUrl}/>


      </div>
    )
  }
}

export default App;
